query:
mutation accountManagementAddAccount ( $addAccountInput:AddAccountInput! ) {
	accountManagement ( ) {
		addAccount ( input:$addAccountInput  )  {
			id
			name
			description
			timeZone
			typeAccountInfo: type
			tenancyAccountInfo: tenancy
			auditAccountInfo: audit {
				createdBy
				createdTime
			}
			planAccountInfo: plan
			statusAccountInfo: status
		}

	}	
}
