query:
mutation accountManagementUpdateAccount ( $updateAccountInput:UpdateAccountInput! ) {
	accountManagement ( ) {
		updateAccount ( input:$updateAccountInput  )  {
			id
			name
			description
			timeZone
			typeAccountInfo: type
			tenancyAccountInfo: tenancy
			auditAccountInfo: audit {
				createdBy
				createdTime
			}
			planAccountInfo: plan
			statusAccountInfo: status
		}

	}	
}
