mutation adminUpdateServicePrincipalAdmin ( $adminID:ID! $updateServicePrincipalAdminInput:UpdateServicePrincipalAdminInput! $accountId:ID! ) {
	admin ( accountId:$accountId ) {
		updateServicePrincipalAdmin ( adminID:$adminID  input:$updateServicePrincipalAdminInput  )  {
			adminID
		}

	}	
}
