query:
mutation policyDynamicIpAllocationAddRule ( $dynamicIpAllocationAddRuleInput:DynamicIpAllocationAddRuleInput! $dynamicIpAllocationPolicyMutationInput:DynamicIpAllocationPolicyMutationInput ) {
	policy ( ) {
		dynamicIpAllocation ( input:$dynamicIpAllocationPolicyMutationInput  )  {
			addRuleDynamicIpAllocationPolicyMutations: addRule ( input:$dynamicIpAllocationAddRuleInput   ) {
				ruleDynamicIpAllocationRuleMutationPayload: rule {
					auditDynamicIpAllocationRulePayload: audit {
						updatedTime
						updatedBy
					}
					ruleDynamicIpAllocationRulePayload: rule {
						id
						name
						description
						index
						sectionDynamicIpAllocationRule: section {
							id
							name
						}

						enabled
						sourceDynamicIpAllocationRule: source {
							userDynamicIpAllocationSource: user {
								id
								name
							}
							usersGroupDynamicIpAllocationSource: usersGroup {
								id
								name
							}
						}

						platformDynamicIpAllocationRule: platform
						countryDynamicIpAllocationRule: country {
							id
							name
						}

						rangeDynamicIpAllocationRule: range {
							globalIpRangeDynamicIpAllocationRange: globalIpRange {
								id
								name
							}
						}

					}
					propertiesDynamicIpAllocationRulePayload: properties
				}

				statusDynamicIpAllocationRuleMutationPayload: status
				errorsDynamicIpAllocationRuleMutationPayload: errors {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
