query:
mutation policyInternetFirewallAddSection ( $policyAddSectionInput:PolicyAddSectionInput! $internetFirewallPolicyMutationInput:InternetFirewallPolicyMutationInput ) {
	policy ( ) {
		internetFirewall ( input:$internetFirewallPolicyMutationInput  )  {
			addSectionInternetFirewallPolicyMutations: addSection ( input:$policyAddSectionInput   ) {
				sectionPolicySectionMutationPayload: section {
					auditPolicySectionPayload: audit {
						updatedTime
						updatedBy
					}
					sectionPolicySectionPayload: section {
						id
						name
					}
					propertiesPolicySectionPayload: properties
				}

				statusPolicySectionMutationPayload: status
				errorsPolicySectionMutationPayload: errors {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
