query:
mutation policyInternetFirewallMoveSection ( $policyMoveSectionInput:PolicyMoveSectionInput! $internetFirewallPolicyMutationInput:InternetFirewallPolicyMutationInput $accountId:ID! ) {
	policy ( accountId:$accountId ) {
		internetFirewall ( input:$internetFirewallPolicyMutationInput  )  {
			moveSectionInternetFirewallPolicyMutations: moveSection ( input:$policyMoveSectionInput   ) {
				section  {
					auditPolicySectionPayload: audit {
						updatedTime 
						updatedBy 
					}
					sectionPolicySectionPayload: section {
						id 
						name 
					}
					propertiesPolicySectionPayload: properties
				}

				status 
				errors  {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
