query:
mutation policyRemotePortFwdAddRule ( $remotePortFwdAddRuleInput:RemotePortFwdAddRuleInput! $remotePortFwdPolicyMutationInput:RemotePortFwdPolicyMutationInput $accountId:ID! ) {
	policy ( accountId:$accountId ) {
		remotePortFwd ( input:$remotePortFwdPolicyMutationInput  )  {
			addRuleRemotePortFwdPolicyMutations: addRule ( input:$remotePortFwdAddRuleInput   ) {
				rule  {
					auditRemotePortFwdRulePayload: audit {
						updatedTime 
						updatedBy 
					}
					ruleRemotePortFwdRulePayload: rule {
						id 
						name 
						description 
						index 
						section  {
							id
							name
						}

						enabled 
						forwardIcmp 
						externalIp  {
							id
							name
						}

						externalPortRange  {
							from
							to
						}

						internalIp 
						internalPortRange  {
							from
							to
						}

						remoteIPs  {
							ip
							ipRangeRemotePortFwdRemoteIps: ipRange {
								from 
								to 
							}
							subnet
							globalIpRangeRemotePortFwdRemoteIps: globalIpRange {
								id 
								name 
							}
						}

						restrictionType 
						tracking  {
							enabled
							frequencyPolicyRuleTrackingAlert: frequency
							subscriptionGroupPolicyRuleTrackingAlert: subscriptionGroup {
								id 
								name 
							}
							webhookPolicyRuleTrackingAlert: webhook {
								id 
								name 
							}
							mailingListPolicyRuleTrackingAlert: mailingList {
								id 
								name 
							}
						}

					}
					propertiesRemotePortFwdRulePayload: properties
				}

				status 
				errors  {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
