query:
mutation policyRemotePortFwdAddSection ( $policyAddSectionInput:PolicyAddSectionInput! $remotePortFwdPolicyMutationInput:RemotePortFwdPolicyMutationInput ) {
	policy ( ) {
		remotePortFwd ( input:$remotePortFwdPolicyMutationInput  )  {
			addSectionRemotePortFwdPolicyMutations: addSection ( input:$policyAddSectionInput   ) {
				sectionPolicySectionMutationPayload: section {
					auditPolicySectionPayload: audit {
						updatedTime
						updatedBy
					}
					sectionPolicySectionPayload: section {
						id
						name
					}
					propertiesPolicySectionPayload: properties
				}

				statusPolicySectionMutationPayload: status
				errorsPolicySectionMutationPayload: errors {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
