query:
mutation policyRemotePortFwdAddSection ( $policyAddSectionInput:PolicyAddSectionInput! $remotePortFwdPolicyMutationInput:RemotePortFwdPolicyMutationInput $accountId:ID! ) {
	policy ( accountId:$accountId ) {
		remotePortFwd ( input:$remotePortFwdPolicyMutationInput  )  {
			addSectionRemotePortFwdPolicyMutations: addSection ( input:$policyAddSectionInput   ) {
				section  {
					auditPolicySectionPayload: audit {
						updatedTime 
						updatedBy 
					}
					sectionPolicySectionPayload: section {
						id 
						name 
					}
					propertiesPolicySectionPayload: properties
				}

				status 
				errors  {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
