query:
mutation policySocketLanUpdateSection ( $policyUpdateSectionInput:PolicyUpdateSectionInput! $socketLanPolicyMutationInput:SocketLanPolicyMutationInput ) {
	policy ( ) {
		socketLan ( input:$socketLanPolicyMutationInput  )  {
			updateSectionSocketLanPolicyMutations: updateSection ( input:$policyUpdateSectionInput   ) {
				sectionPolicySectionMutationPayload: section {
					auditPolicySectionPayload: audit {
						updatedTime
						updatedBy
					}
					sectionPolicySectionPayload: section {
						id
						name
					}
					propertiesPolicySectionPayload: properties
				}

				statusPolicySectionMutationPayload: status
				errorsPolicySectionMutationPayload: errors {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
