query:
mutation policyTerminalServerAddRule ( $terminalServerAddRuleInput:TerminalServerAddRuleInput! $terminalServerPolicyMutationInput:TerminalServerPolicyMutationInput $accountId:ID! ) {
	policy ( accountId:$accountId ) {
		terminalServer ( input:$terminalServerPolicyMutationInput  )  {
			addRuleTerminalServerPolicyMutations: addRule ( input:$terminalServerAddRuleInput   ) {
				rule  {
					auditTerminalServerRulePayload: audit {
						updatedTime 
						updatedBy 
					}
					ruleTerminalServerRulePayload: rule {
						id 
						name 
						description 
						index 
						section  {
							id
							name
						}

						enabled 
						allowedHostIP  {
							id
							name
						}

						excludeTraffic  {
							id
							name
						}

					}
					propertiesTerminalServerRulePayload: properties
				}

				status 
				errors  {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
