query:
mutation policyTerminalServerAddRule ( $terminalServerAddRuleInput:TerminalServerAddRuleInput! $terminalServerPolicyMutationInput:TerminalServerPolicyMutationInput ) {
	policy ( ) {
		terminalServer ( input:$terminalServerPolicyMutationInput  )  {
			addRuleTerminalServerPolicyMutations: addRule ( input:$terminalServerAddRuleInput   ) {
				ruleTerminalServerRuleMutationPayload: rule {
					auditTerminalServerRulePayload: audit {
						updatedTime
						updatedBy
					}
					ruleTerminalServerRulePayload: rule {
						id
						name
						description
						index
						sectionTerminalServerRule: section {
							id
							name
						}

						enabled
						allowedHostIPTerminalServerRule: allowedHostIP {
							id
							name
						}

						excludeTrafficTerminalServerRule: excludeTraffic {
							id
							name
						}

					}
					propertiesTerminalServerRulePayload: properties
				}

				statusTerminalServerRuleMutationPayload: status
				errorsTerminalServerRuleMutationPayload: errors {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
