query:
mutation policyWanFirewallAddSection ( $policyAddSectionInput:PolicyAddSectionInput! $wanFirewallPolicyMutationInput:WanFirewallPolicyMutationInput ) {
	policy ( ) {
		wanFirewall ( input:$wanFirewallPolicyMutationInput  )  {
			addSectionWanFirewallPolicyMutations: addSection ( input:$policyAddSectionInput   ) {
				sectionPolicySectionMutationPayload: section {
					auditPolicySectionPayload: audit {
						updatedTime
						updatedBy
					}
					sectionPolicySectionPayload: section {
						id
						name
					}
					propertiesPolicySectionPayload: properties
				}

				statusPolicySectionMutationPayload: status
				errorsPolicySectionMutationPayload: errors {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
