query:
mutation policyWanFirewallMoveSection ( $policyMoveSectionInput:PolicyMoveSectionInput! $wanFirewallPolicyMutationInput:WanFirewallPolicyMutationInput $accountId:ID! ) {
	policy ( accountId:$accountId ) {
		wanFirewall ( input:$wanFirewallPolicyMutationInput  )  {
			moveSectionWanFirewallPolicyMutations: moveSection ( input:$policyMoveSectionInput   ) {
				section  {
					auditPolicySectionPayload: audit {
						updatedTime 
						updatedBy 
					}
					sectionPolicySectionPayload: section {
						id 
						name 
					}
					propertiesPolicySectionPayload: properties
				}

				status 
				errors  {
					errorMessage
					errorCode
				}

			}
		}

	}	
}
