query:
mutation sitesRemoveSocketAddOnCard ( $accountId:ID! $removeSocketAddOnCardInput:RemoveSocketAddOnCardInput!) {
	sites ( accountId:$accountId ) {
		removeSocketAddOnCard ( input:$removeSocketAddOnCardInput  )  {
			addOns {
				expansionSlotNumber
	type {
		name
		recommendedAction
		details
	}
			}
		}
	}	
}
