query:
mutation sitesUpdateNetworkRange ( $accountId:ID! $networkRangeId:ID! $updateNetworkRangeInput:UpdateNetworkRangeInput!) {
	sites ( accountId:$accountId networkRangeId:$networkRangeId input:$updateNetworkRangeInput ) {
		updateNetworkRange ( networkRangeId:$networkRangeId  input:$updateNetworkRangeInput  )  {
			networkRangeId
		}
	}	
}
