query:
mutation sitesUpdateSiteSocketConfiguration ( $accountId:ID! $updateSiteSocketConfigurationInput:UpdateSiteSocketConfigurationInput!) {
	sites ( accountId:$accountId ) {
		updateSiteSocketConfiguration ( input:$updateSiteSocketConfigurationInput  )  {
			siteSocketConfiguration {
				primarySocketConfiguration {
					description
				}
				secondarySocketConfiguration {
					description
				}
			}
		}
	}	
}
