	xdr ( accountId:$accountId input:$addStoryCommentInput ) {
mutation.xdr.addStoryCommentaddStoryComment ( input:$addStoryCommentInput  )  {
mutation.xdr.addStoryComment	comment {
mutation.xdr.addStoryComment		id
mutation.xdr.addStoryComment		createdAt
mutation.xdr.addStoryComment		text
mutation.xdr.addStoryComment		actor {
mutation.xdr.addStoryComment			id
mutation.xdr.addStoryComment			name
mutation.xdr.addStoryComment			... on AdminRef {
mutation.xdr.addStoryComment				id
mutation.xdr.addStoryComment				name
mutation.xdr.addStoryComment			}
mutation.xdr.addStoryComment			... on ApiKeyRef {
mutation.xdr.addStoryComment				id
mutation.xdr.addStoryComment				name
mutation.xdr.addStoryComment			}
mutation.xdr.addStoryComment		}
mutation.xdr.addStoryComment	}
mutation.xdr.addStoryComment}
	}	
}
