query:
mutation xdrDeleteStoryComment ( $deleteStoryCommentInput:DeleteStoryCommentInput! $accountId:ID! ) {
	xdr ( accountId:$accountId ) {
		deleteStoryComment ( input:$deleteStoryCommentInput  )  {
			commentDeleteStoryCommentPayload: comment {
				id 
				createdAt 
				text 
				actor  {
					id
					name
					... on AdminRef {
						id 
						name 
					}
					... on ApiKeyRef {
						id 
						name 
					}
				}

			}
		}

	}	
}
