query:
query catalogs ( $applicationRefInput:ApplicationRefInput! $catalogApplicationListInput:CatalogApplicationListInput! $catalogApplicationContentTypeGroupListInput:CatalogApplicationContentTypeGroupListInput! ) {
	catalogs ( ) {
		catalogApplication ( input:$applicationRefInput  )  {
			id
			name
			descriptionSummary
			description
			categoryCatalogApplication: category {
				id
				name
			}
			complianceAttributesCatalogApplication: complianceAttributes {
				iso27001CatalogApplicationComplianceAttributes: iso27001
				soxCatalogApplicationComplianceAttributes: sox
				hippaCatalogApplicationComplianceAttributes: hippa
				soc1CatalogApplicationComplianceAttributes: soc1
				soc2CatalogApplicationComplianceAttributes: soc2
				soc3CatalogApplicationComplianceAttributes: soc3
				isae3402CatalogApplicationComplianceAttributes: isae3402
				pciDssCatalogApplicationComplianceAttributes: pciDss
			}
			securityAttributesCatalogApplication: securityAttributes {
				mfaCatalogApplicationSecurityAttributes: mfa
				encryptionAtRestCatalogApplicationSecurityAttributes: encryptionAtRest
				auditTrailCatalogApplicationSecurityAttributes: auditTrail
				rbacCatalogApplicationSecurityAttributes: rbac
				rememberPasswordCatalogApplicationSecurityAttributes: rememberPassword
				ssoCatalogApplicationSecurityAttributes: sso
				trustedCertificateCatalogApplicationSecurityAttributes: trustedCertificate
				tlsEnforcementCatalogApplicationSecurityAttributes: tlsEnforcement
				httpSecurityHeadersCatalogApplicationSecurityAttributes: httpSecurityHeaders
			}
			ipoStatus
			website
			originCountryCatalogApplication: originCountry {
				id
				name
			}
			region
			city
			risk
			numOfEmployeesCatalogApplication: numOfEmployees
			capabilityCatalogApplication: capability
			activityCatalogApplication: activity {
				id
				name
				fieldsCatalogApplicationActivity: fields {
					id
					name
					possibleOperatorsCatalogApplicationActivityField: possibleOperators
					possibleValues
				}

			}
			typeCatalogApplication: type
			sanctioned
			recentlyAdded
			standardPortsCatalogApplication: standardPorts {
				port
				portRangeCustomService: portRange {
					from
					to
				}

				protocolCustomService: protocol
			}
		}

		catalogApplicationList ( input:$catalogApplicationListInput  )  {
			applicationCatalogApplicationListPayload: application {
				id
				name
				descriptionSummary
				description
				categoryCatalogApplication: category {
					id
					name
				}

				complianceAttributesCatalogApplication: complianceAttributes {
					iso27001CatalogApplicationComplianceAttributes: iso27001
					soxCatalogApplicationComplianceAttributes: sox
					hippaCatalogApplicationComplianceAttributes: hippa
					soc1CatalogApplicationComplianceAttributes: soc1
					soc2CatalogApplicationComplianceAttributes: soc2
					soc3CatalogApplicationComplianceAttributes: soc3
					isae3402CatalogApplicationComplianceAttributes: isae3402
					pciDssCatalogApplicationComplianceAttributes: pciDss
				}

				securityAttributesCatalogApplication: securityAttributes {
					mfaCatalogApplicationSecurityAttributes: mfa
					encryptionAtRestCatalogApplicationSecurityAttributes: encryptionAtRest
					auditTrailCatalogApplicationSecurityAttributes: auditTrail
					rbacCatalogApplicationSecurityAttributes: rbac
					rememberPasswordCatalogApplicationSecurityAttributes: rememberPassword
					ssoCatalogApplicationSecurityAttributes: sso
					trustedCertificateCatalogApplicationSecurityAttributes: trustedCertificate
					tlsEnforcementCatalogApplicationSecurityAttributes: tlsEnforcement
					httpSecurityHeadersCatalogApplicationSecurityAttributes: httpSecurityHeaders
				}

				ipoStatus
				website
				originCountryCatalogApplication: originCountry {
					id
					name
				}

				region
				city
				risk
				numOfEmployeesCatalogApplication: numOfEmployees
				capabilityCatalogApplication: capability
				activityCatalogApplication: activity {
					id
					name
					fieldsCatalogApplicationActivity: fields {
						id
						name
						possibleOperatorsCatalogApplicationActivityField: possibleOperators
						possibleValues
					}
				}

				typeCatalogApplication: type
				sanctioned
				recentlyAdded
				standardPortsCatalogApplication: standardPorts {
					port
					portRangeCustomService: portRange {
						from
						to
					}
					protocolCustomService: protocol
				}

			}
			pageInfoCatalogApplicationListPayload: pageInfo {
				total
			}
		}

		contentTypeGroupList ( input:$catalogApplicationContentTypeGroupListInput  )  {
			contentTypeGroupCatalogApplicationContentTypeGroupListPayload: contentTypeGroup {
				id
				name
				contentTypeCatalogApplicationContentTypeGroup: contentType {
					id
					name
				}

			}
			pageInfoCatalogApplicationContentTypeGroupListPayload: pageInfo {
				total
			}
		}

	}	
}
