query policyDynamicIpAllocationPolicy ( $dynamicIpAllocationPolicyInput:DynamicIpAllocationPolicyInput ) {
	policy ( ) {
		dynamicIpAllocation {
			policyDynamicIpAllocationPolicyQueries: policy ( input:$dynamicIpAllocationPolicyInput   ) {
				enabled
				rulesDynamicIpAllocationPolicy: rules {
					auditDynamicIpAllocationRulePayload: audit {
						updatedTime
						updatedBy
					}
					ruleDynamicIpAllocationRulePayload: rule {
						id
						name
						description
						index
						sectionDynamicIpAllocationRule: section {
							id
							name
						}

						enabled
						sourceDynamicIpAllocationRule: source {
							userDynamicIpAllocationSource: user {
								id
								name
							}
							usersGroupDynamicIpAllocationSource: usersGroup {
								id
								name
							}
						}

						platformDynamicIpAllocationRule: platform
						countryDynamicIpAllocationRule: country {
							id
							name
						}

						rangeDynamicIpAllocationRule: range {
							globalIpRangeDynamicIpAllocationRange: globalIpRange {
								id
								name
							}
						}

					}
					propertiesDynamicIpAllocationRulePayload: properties
				}

				sectionsDynamicIpAllocationPolicy: sections {
					auditPolicySectionPayload: audit {
						updatedTime
						updatedBy
					}
					sectionPolicySectionPayload: section {
						id
						name
					}
					propertiesPolicySectionPayload: properties
				}

				auditDynamicIpAllocationPolicy: audit {
					publishedTime
					publishedBy
				}

				revisionDynamicIpAllocationPolicy: revision {
					id
					name
					description
					changes
					createdTime
					updatedTime
				}

			}
		}

	}	
}
