query policyTerminalServerPolicy ( $terminalServerPolicyInput:TerminalServerPolicyInput ) {
	policy ( ) {
		terminalServer {
			policyTerminalServerPolicyQueries: policy ( input:$terminalServerPolicyInput   ) {
				enabled
				rulesTerminalServerPolicy: rules {
					auditTerminalServerRulePayload: audit {
						updatedTime
						updatedBy
					}
					ruleTerminalServerRulePayload: rule {
						id
						name
						description
						index
						sectionTerminalServerRule: section {
							id
							name
						}

						enabled
						allowedHostIPTerminalServerRule: allowedHostIP {
							id
							name
						}

						excludeTrafficTerminalServerRule: excludeTraffic {
							id
							name
						}

					}
					propertiesTerminalServerRulePayload: properties
				}

				sectionsTerminalServerPolicy: sections {
					auditPolicySectionPayload: audit {
						updatedTime
						updatedBy
					}
					sectionPolicySectionPayload: section {
						id
						name
					}
					propertiesPolicySectionPayload: properties
				}

				auditTerminalServerPolicy: audit {
					publishedTime
					publishedBy
				}

				revisionTerminalServerPolicy: revision {
					id
					name
					description
					changes
					createdTime
					updatedTime
				}

			}
		}

	}	
}
