query:
query siteBgpPeer ( $bgpPeerRefInput:BgpPeerRefInput! $accountId:ID! ) {
	site ( accountId:$accountId ) {
		bgpPeer ( input:$bgpPeerRefInput  )  {
			siteBgpPeer: site {
				id 
				name 
			}
			id
			name
			peerAsn
			catoAsn
			peerIp
			catoIp
			advertiseDefaultRoute
			advertiseAllRoutes
			advertiseSummaryRoutes
			summaryRouteBgpPeer: summaryRoute {
				id 
				route 
				community  {
					from
					to
				}

			}
			defaultActionBgpPeer: defaultAction
			defaultActionExclusionBgpPeer: defaultActionExclusion {
				bgpRouteExactFilterRule  {
					id
					globalIpRangeBgpRouteExactFilterRule: globalIpRange {
						id 
						name 
					}
					networkSubnet
				}

				bgpRouteExactAndInclusiveFilterRule  {
					id
					globalIpRangeBgpRouteExactAndInclusiveFilterRule: globalIpRange {
						id 
						name 
					}
					globalIpRangeExceptionBgpRouteExactAndInclusiveFilterRule: globalIpRangeException {
						id 
						name 
					}
					networkSubnet
					networkSubnetException
					le
					ge
				}

				communityFilterRule  {
					id
					predicateBgpCommunityFilterRule: predicate
					communityBgpCommunityFilterRule: community {
						from 
						to 
					}
				}

			}
			performNat
			md5AuthKey
			metric
			holdTime
			keepaliveInterval
			bfdEnabled
			bfdSettingsBgpPeer: bfdSettings {
				transmitInterval 
				receiveInterval 
				multiplier 
			}
			trackingBgpPeer: tracking {
				id 
				enabled 
				alertFrequency 
				subscriptionId 
			}
		}

	}	
}
