query siteCloudInterconnectPhysicalConnectionId ( $accountId:ID! $cloudInterconnectPhysicalConnectionIdInput:CloudInterconnectPhysicalConnectionIdInput!) {
	site ( accountId:$accountId ) {
		cloudInterconnectPhysicalConnectionId ( input:$cloudInterconnectPhysicalConnectionIdInput  )  {
id {
	id
	status
	description
	firstSignal
	lastSignal
	engineType
	vendor
	producer
	producerName
	connectionType
	indication
	queryName
	source
	criticality
	ticket
	research
	siteName
	storyDuration
	analystFeedback {
		verdict
		severity
		threatClassification
		additionalInfo
		threatType {
			name
			recommendedAction
			details
		}
	}
	site {
		id
		name
	}
	user {
		id
		name
	}
	similarStoriesData {
		storyId
		threatTypeName
		verdict
		threatClassification
		similarityPercentage
		indication
	}
	networkIncidentTimeline {
		description
		created
		validated
		eventType
		incidentId
		networkEventSource
		eventIds
		acknowledged
		linkId
		linkName
		linkConfigPrecedence
		linkStatus
		linkConfigBandwidth
		deviceConfigHaRole
		deviceHaRoleState
		socketSerialId
		bgpConnection {
			connectionName
			peerIp
			peerAsn
			catoIp
			catoAsn
		}
		linkQualityIssue {
			issueType
			direction
			current
			threshold
		}
	}
	bgpConnection {
		connectionName
		peerIp
		peerAsn
		catoIp
		catoAsn
	}
	ilmmDetails {
		linkDetails {
			description
			linkId
			ispLinkId
			comments
			onboardingStatus
			activeLicense
		}
		ispDetails {
			name
			description
			ispAccountId
			supportEmail
			supportPhone
			countryCode
			loaFile {
				fileName
				fileHash
				uploadedAt
			}
		}
		contacts {
			name
			phone
			email
		}
	}
}
		}
	}	
}
