query siteSiteSocketConfiguration ( $accountId:ID! $siteSocketConfigurationInput:SiteSocketConfigurationInput!) {
	site ( accountId:$accountId ) {
		siteSocketConfiguration ( input:$siteSocketConfigurationInput  )  {
			primarySocketConfiguration {
				description
			}
			secondarySocketConfiguration {
				description
			}
		}
	}	
}
