query:
query socketPortMetrics ( $accountID:ID! $timeFrame:TimeFrame! $socketPortMetricsMeasure:[SocketPortMetricsMeasure] $socketPortMetricsDimension:[SocketPortMetricsDimension] $socketPortMetricsFilter:[SocketPortMetricsFilter!] $socketPortMetricsSort:[SocketPortMetricsSort!]) {
	socketPortMetrics ( accountID:$accountID timeFrame:$timeFrame measures:$socketPortMetricsMeasure dimensions:$socketPortMetricsDimension filters:$socketPortMetricsFilter sort:$socketPortMetricsSort ) {
id {
	id
	status
	description
	firstSignal
	lastSignal
	engineType
	vendor
	producer
	producerName
	connectionType
	indication
	queryName
	source
	criticality
	ticket
	research
	siteName
	storyDuration
	analystFeedback {
		verdict
		severity
		threatClassification
		additionalInfo
		threatType {
			name
			recommendedAction
			details
		}
	}
	site {
		id
		name
	}
	user {
		id
		name
	}
	similarStoriesData {
		storyId
		threatTypeName
		verdict
		threatClassification
		similarityPercentage
		indication
	}
	networkIncidentTimeline {
		description
		created
		validated
		eventType
		incidentId
		networkEventSource
		eventIds
		acknowledged
		linkId
		linkName
		linkConfigPrecedence
		linkStatus
		linkConfigBandwidth
		deviceConfigHaRole
		deviceHaRoleState
		socketSerialId
		bgpConnection {
			connectionName
			peerIp
			peerAsn
			catoIp
			catoAsn
		}
		linkQualityIssue {
			issueType
			direction
			current
			threshold
		}
	}
	bgpConnection {
		connectionName
		peerIp
		peerAsn
		catoIp
		catoAsn
	}
	ilmmDetails {
		linkDetails {
			description
			linkId
			ispLinkId
			comments
			onboardingStatus
			activeLicense
		}
		ispDetails {
			name
			description
			ispAccountId
			supportEmail
			supportPhone
			countryCode
			loaFile {
				fileName
				fileHash
				uploadedAt
			}
		}
		contacts {
			name
			phone
			email
		}
	}
}
from {
	container {
		id
		name
		description
		size
		audit {
			createdAt
			createdBy
			lastModifiedBy
			lastModifiedAt
		}
	}
}
to {
	comment {
		id
		createdAt
		text
		actor {
			__typename
			... on AdminRef {
				id
				name
			}
			... on ApiKeyRef {
				id
				name
			}
		}
	}
}
		total
		totals
		records {
			fieldsUnitTypes
			fieldsMap
			trends
			prevTimeFrame
		}
	}	
}
